var DemoApp=angular.module("DemoApp",["ngRoute","ngAnimate","ngResource","ngSanitize"]).config(["$routeProvider","$locationProvider",function(e){e.when("/",{title:"Welcome",templateUrl:"/partials/index.html",controller:"GetPage"}).when("/about",{title:"About Site",templateUrl:"/partials/page.html",controller:"GetPage"}).when("/services",{title:"Our Services",templateUrl:"/partials/page.html",controller:"GetPage"}).when("/blog",{title:"Read Our Blog",templateUrl:"/partials/blog.html",controller:"BlogList"}).when("/blog/:category",{title:"",templateUrl:"/partials/blog.html",controller:"BlogList"}).when("/blog/:category/:post",{title:"",templateUrl:"/partials/post.html",controller:"BlogPost"})}]).run(["$location","$rootScope",function(e,t){t.$on("$routeChangeSuccess",function(e,o){t.title=o.$$route.title})}]).controller("MenuController",function(e,t){e.isActive=function(e){return e===t.url()}}).controller("GetPage",function(e,t,o){t.get("/wordpress/api/get_page/?slug="+o.url()).success(function(t){e.page=t.page}).error(function(){window.alert("We have been unable to access the feed :-(")})}).controller("BlogList",function(e,t,o){if(o.category)var r=t.get("/wordpress/api/get_category_posts/?slug="+o.category);else var r=t.get("/wordpress/api/get_posts");r.success(function(t){e.posts=t.posts}).error(function(){window.alert("We have been unable to access the feed :-(")})}).controller("BlogPost",function(e,t,o){t.get("/wordpress/api/get_post/?slug="+o.post).success(function(t){e.post=t}).error(function(){window.alert("We have been unable to access the feed :-(")})}).controller("CategoryList",function(e,t){t.get("/wordpress/api/get_category_index").success(function(t){e.categories=t.categories}).error(function(){window.alert("We have been unable to access the feed :-(")})});